import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react'; 

import 'swiper/swiper-bundle.css';

import slidess from './Mock';

const SimpleSlider = ({ Slide }) => {
  return (
    <Swiper      
      spaceBetween={50}
      slidesPerView={5}
      navigation={true}
      pagination={{ clickable: true }}
      effect="cube"
      grabCursor={true}
      cubeEffect={{
        shadow: true,
        slideShadows: true,
        shadowOffset: 20,
        shadowScale: 0.94,
      }}
      mousewheel={{
        invert: true,
      }}
      breakpoints={{
        200: {
          slidesPerView: 1,
          spaceBetween: 20,
        },
        640: {
          slidesPerView: 1,
          spaceBetween: 20,
        },
        768: {
          slidesPerView: 3,
          spaceBetween: 40,
        },
        1024: {
          slidesPerView: 4,
          spaceBetween: 50,
        },
      }}
      onSwiper={(swiper) => console.log(swiper)}
      onSlideChange={() => console.log('slide change')}
    >
      {slidess.map((slide) => (
        <SwiperSlide key={slide.image}>
          <img src={slide.image} alt={slide.title} width="100%" />
          <h2>{slide.title}</h2>
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default Slider;
